mapper.select:
select name, ing_photo, ing_detail
from cock_ingre cross join ingredient i on i.ing_num = cock_ingre.ing_num
where ing_name="#{name}";

SELECT c.name, c.name_eng, c.ABV, c.cocktail_detail, c.recipe, c.cocktails, c.cocktail_img, b.tagName AS basetag, t.tagName AS tastetag, s.TagName AS smelltag FROM cocktail c LEFT JOIN basetag b ON c.baseNo = b.tagNo LEFT JOIN tastetag t ON c.tasteNo = t.tagNo LEFT JOIN smelltag s ON c.smellNo = s.TagNo WHERE 1=1
AND b.tagName=#{basetag}
AND t.tagName=#{tastetag}
