SELECT c.name AS cocktail_name
FROM cocktail c
WHERE (
SELECT COUNT(DISTINCT ci.ing_num)
FROM cock_ingre ci
JOIN ingredient i ON ci.ing_num = i.ing_num
WHERE ci.name = c.name AND i.ing_name IN

    #{ingredient}

) = (
SELECT COUNT(DISTINCT ci.ing_num)
FROM cock_ingre ci
WHERE ci.name = c.name
);
